pragma solidity 0.8.20;

import "./FlashMintLiquidatorAaveBase.sol";
import {Constants} from "../shared/Constants.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {ERC4626} from "@openzeppelin/contracts/token/ERC20/extensions/ERC4626.sol";

abstract contract FlashMintLiquidatorAaveBorrowRepayBase is
    FlashMintLiquidatorAaveBase,
    Ownable
{
    using SafeERC20 for ERC20;
    using PercentageMath for uint256;

    uint256 public slippageTolerance; // in basis points units

    event SlippageToleranceSet(uint256 newTolerance);
    error NotSupportingNonDSTABLE(address borrowedToken, string symbol);

    mapping(address => address) private proxyContractMap;

    constructor(
        IERC3156FlashLender _flashMinter,
        ILendingPoolAddressesProvider _addressesProvider,
        ILendingPool _liquidateLender,
        IAToken _aDSTABLE,
        uint256 _slippageTolerance
    )
        FlashMintLiquidatorAaveBase(
            _flashMinter,
            _liquidateLender,
            _addressesProvider,
            _aDSTABLE
        )
        Ownable(msg.sender)
    {
        slippageTolerance = _slippageTolerance;
        emit SlippageToleranceSet(_slippageTolerance);
    }

    function setProxyContract(
        address _collateralUnderlying,
        address _proxyContract
    ) external onlyOwner {
        proxyContractMap[_collateralUnderlying] = _proxyContract;
    }

    function getProxyContract(
        address _collateralUnderlying
    ) public view returns (address) {
        address proxyContract = proxyContractMap[_collateralUnderlying];
        if (proxyContract != address(0)) {
            return proxyContract;
        }
        return _collateralUnderlying;
    }

    function setSlippageTolerance(uint256 _newTolerance) external onlyOwner {
        if (_newTolerance > Constants.ONE_HUNDRED_PERCENT_BPS)
            revert InvalidSlippageTolerance(_newTolerance);

        slippageTolerance = _newTolerance;
        emit SlippageToleranceSet(_newTolerance);
    }

    function liquidate(
        address _poolTokenBorrowedAddress,
        address _poolTokenCollateralAddress,
        address _borrower,
        uint256 _repayAmount,
        bool _stakeTokens,
        bool _isUnstakeCollateralToken,
        bytes memory _swapData
    ) external nonReentrant {
        LiquidateParams memory liquidateParams = LiquidateParams(
            _getUnderlying(_poolTokenCollateralAddress),
            _getUnderlying(_poolTokenBorrowedAddress),
            IAToken(_poolTokenCollateralAddress),
            IAToken(_poolTokenBorrowedAddress),
            msg.sender,
            _borrower,
            _repayAmount,
            _isUnstakeCollateralToken
        );

        uint256 seized;
        address actualCollateralToken;
        if (
            liquidateParams.borrowedUnderlying.balanceOf(address(this)) >=
            _repayAmount
        ) {
            // we can liquidate without flash loan by using the contract balance
            seized = _liquidateInternal(liquidateParams);
        } else {
            FlashLoanParams memory params = FlashLoanParams(
                address(liquidateParams.collateralUnderlying),
                address(liquidateParams.borrowedUnderlying),
                address(liquidateParams.poolTokenCollateral),
                address(liquidateParams.poolTokenBorrowed),
                liquidateParams.liquidator,
                liquidateParams.borrower,
                liquidateParams.toRepay,
                _isUnstakeCollateralToken,
                _swapData
            );
            (seized, actualCollateralToken) = _liquidateWithFlashLoan(params);
        }

        if (!_stakeTokens)
            ERC20(actualCollateralToken).safeTransfer(msg.sender, seized);
    }

    /// @dev ERC-3156 Flash loan callback
    function onFlashLoan(
        address _initiator,
        address,
        uint256, // flashloan amount
        uint256,
        bytes calldata data
    ) external override returns (bytes32) {
        if (msg.sender != address(flashMinter)) revert UnknownLender();
        if (_initiator != address(this)) revert UnknownInitiator();
        FlashLoanParams memory flashLoanParams = _decodeData(data);

        _flashLoanInternal(flashLoanParams);
        return FLASHLOAN_CALLBACK;
    }

    function _flashLoanInternal(
        FlashLoanParams memory _flashLoanParams
    ) internal {
        if (_flashLoanParams.borrowedUnderlying != address(dstable)) {
            revert NotSupportingNonDSTABLE(
                _flashLoanParams.borrowedUnderlying,
                ERC20(_flashLoanParams.borrowedUnderlying).symbol()
            );
        }

        LiquidateParams memory liquidateParams = LiquidateParams(
            ERC20(_flashLoanParams.collateralUnderlying),
            ERC20(_flashLoanParams.borrowedUnderlying),
            IAToken(_flashLoanParams.poolTokenCollateral),
            IAToken(_flashLoanParams.poolTokenBorrowed),
            _flashLoanParams.liquidator,
            _flashLoanParams.borrower,
            _flashLoanParams.toRepay,
            _flashLoanParams.isUnstakeCollateralToken
        );
        uint256 seized = _liquidateInternal(liquidateParams);

        if (
            _flashLoanParams.borrowedUnderlying !=
            _flashLoanParams.collateralUnderlying
        ) {
            (
                address actualCollateralToken,
                address proxyContract
            ) = getActualCollateralToken(
                    _flashLoanParams.collateralUnderlying,
                    _flashLoanParams.isUnstakeCollateralToken
                );
            uint256 actualCollateralAmount = seized;
            // If isUnstakeCollateralToken is true, we need to unstake the collateral to its underlying token
            if (_flashLoanParams.isUnstakeCollateralToken) {
                // Approve to burn the shares
                ERC20(_flashLoanParams.collateralUnderlying).approve(
                    proxyContract,
                    actualCollateralAmount
                );

                actualCollateralAmount = redeemERC4626Token(
                    proxyContract,
                    actualCollateralAmount,
                    address(this)
                );
            }

            // need a swap
            // we use aave oracle
            IPriceOracleGetter oracle = IPriceOracleGetter(
                addressesProvider.getPriceOracle()
            );

            // Convert toRepay amount from borrowedUnderlying to collateral token amount using oracle prices
            uint256 maxIn = (((_flashLoanParams.toRepay *
                10 ** ERC20(actualCollateralToken).decimals() *
                oracle.getAssetPrice(_flashLoanParams.borrowedUnderlying)) /
                (oracle.getAssetPrice(actualCollateralToken) *
                    10 ** liquidateParams.borrowedUnderlying.decimals())) *
                (Constants.ONE_HUNDRED_PERCENT_BPS + slippageTolerance)) /
                Constants.ONE_HUNDRED_PERCENT_BPS;

            _swapExactOutput(
                actualCollateralToken,
                _flashLoanParams.borrowedUnderlying,
                _flashLoanParams.swapData,
                _flashLoanParams.toRepay,
                maxIn
            );
        }
        emit Liquidated(
            _flashLoanParams.liquidator,
            _flashLoanParams.borrower,
            _flashLoanParams.poolTokenBorrowed,
            _flashLoanParams.poolTokenCollateral,
            _flashLoanParams.toRepay,
            seized,
            true
        );
    }

    function redeemERC4626Token(
        address _collateralERC4626Token,
        uint256 _amount,
        address _recipient
    ) public returns (uint256) {
        return
            ERC4626(_collateralERC4626Token).redeem(
                _amount,
                _recipient,
                _recipient
            );
    }

    function getActualCollateralToken(
        address _collateralUnderlying,
        bool _isUnstakeCollateralToken
    )
        public
        view
        override
        returns (address actualCollateralToken_, address proxyContract_)
    {
        if (_isUnstakeCollateralToken) {
            proxyContract_ = getProxyContract(_collateralUnderlying);
            actualCollateralToken_ = ERC4626(proxyContract_).asset();
        } else {
            actualCollateralToken_ = _collateralUnderlying;
        }
        // If not unstake, the proxyContract_ is zero address
        return (actualCollateralToken_, proxyContract_);
    }

    /// @dev Swap exact output amount of tokens (need to override this method)
    /// @param _inputToken address of the token to swap
    /// @param _outputToken address of the token to swap
    /// @param _swapData swap data
    /// @param _amount amount of tokens to swap
    /// @param _maxIn maximum amount of input tokens
    /// @return amountIn amount of input tokens
    function _swapExactOutput(
        address _inputToken,
        address _outputToken,
        bytes memory _swapData,
        uint256 _amount,
        uint256 _maxIn
    ) internal virtual returns (uint256 amountIn);
}
","keccak256":"0xabf14a7c7b11bc703cb2cc08cf01ae84cdee51a56c0a04ec27e31759fcbc7397","license":"GNU AGPLv3"},"contracts/aave-v3/FlashMintLiquidatorAaveBorrowRepayPTOdos.sol":{"content":"// SPDX-License-Identifier: GNU AGPLv3
pragma solidity 0.8.20;

import "../odos/interface/IOdosRouterV2.sol";
import "../odos/OdosSwapUtils.sol";
import "../pendle/PendleSwapUtils.sol";
import "./FlashMintLiquidatorAaveBorrowRepayBase.sol";

/**
 * @title FlashMintLiquidatorAaveBorrowRepayPTOdos
 * @notice Flash mint liquidator contract that handles PT token liquidations with Pendle + Odos swaps
 * @dev This contract performs a two-stage swap:
 *      1. PT token → underlying asset (via Pendle)
 *      2. underlying asset → target token (via Odos)
 */
contract FlashMintLiquidatorAaveBorrowRepayPTOdos is
    FlashMintLiquidatorAaveBorrowRepayBase
{
    using SafeERC20 for ERC20;

    /// @notice Data structure for encoding PT swap parameters
    struct PTSwapData {
        address underlyingAsset; // Underlying asset from PT swap
        address pendleRouter; // Pendle router address
        bytes pendleCalldata; // Transaction data from Pendle SDK
        address odosRouter; // Odos router address
        bytes odosCalldata; // Transaction data from Odos API (can be empty if no second swap needed)
    }

    /// @notice Custom errors
    error InsufficientOutputAfterSwap(uint256 expected, uint256 actual);
    error InvalidPTSwapData();
    error OdosSwapFailed(string reason);

    /// @notice Events
    event PTSwapExecuted(
        address indexed ptToken,
        address indexed underlyingToken,
        uint256 ptAmount,
        uint256 underlyingReceived
    );

    event TwoStageSwapExecuted(
        address indexed ptToken,
        address indexed underlyingToken,
        address indexed targetToken,
        uint256 ptAmount,
        uint256 underlyingReceived,
        uint256 targetReceived
    );

    /// @notice Immutable contract references
    IOdosRouterV2 public immutable odosRouter;

    constructor(
        IERC3156FlashLender _flashMinter,
        ILendingPoolAddressesProvider _addressesProvider,
        ILendingPool _liquidateLender,
        IAToken _aDSTABLE,
        uint256 _slippageTolerance,
        IOdosRouterV2 _odosRouter
    )
        FlashMintLiquidatorAaveBorrowRepayBase(
            _flashMinter,
            _addressesProvider,
            _liquidateLender,
            _aDSTABLE,
            _slippageTolerance
        )
    {
        odosRouter = _odosRouter;
    }

    /**
     * @notice Executes a two-stage swap: PT token → underlying → target token
     * @param _inputToken The PT token address
     * @param _outputToken The final target token address
     * @param _swapData Encoded PTSwapData containing both Pendle and Odos swap parameters
     * @param _amount The exact amount of output tokens required
     * @param _maxIn The maximum amount of PT tokens to spend
     * @return amountIn The actual amount of PT tokens used
     */
    function _swapExactOutput(
        address _inputToken,
        address _outputToken,
        bytes memory _swapData,
        uint256 _amount,
        uint256 _maxIn
    ) internal override returns (uint256 amountIn) {
        // Decode PT swap data
        PTSwapData memory ptSwapData = abi.decode(_swapData, (PTSwapData));

        // Validate swap data
        if (ptSwapData.underlyingAsset == address(0)) {
            revert InvalidPTSwapData();
        }

        // Stage 1: Execute Pendle swap (PT → underlying)
        uint256 underlyingReceived = _executePendleSwap(
            _inputToken,
            _maxIn,
            ptSwapData.pendleRouter,
            ptSwapData.pendleCalldata
        );

        emit PTSwapExecuted(
            _inputToken,
            ptSwapData.underlyingAsset,
            _maxIn,
            underlyingReceived
        );

        // Stage 2: Execute Odos swap (underlying → target) or handle direct case
        uint256 targetReceived;
        if (ptSwapData.underlyingAsset == _outputToken) {
            // Direct case: underlying asset is the target token
            targetReceived = underlyingReceived;
        } else {
            // Need second swap via Odos
            if (
                ptSwapData.odosRouter == address(0) ||
                ptSwapData.odosCalldata.length == 0
            ) {
                revert InvalidPTSwapData();
            }

            targetReceived = _executeOdosSwap(
                ptSwapData.underlyingAsset,
                _outputToken,
                underlyingReceived,
                _amount,
                ptSwapData.odosRouter,
                ptSwapData.odosCalldata
            );
        }

        emit TwoStageSwapExecuted(
            _inputToken,
            ptSwapData.underlyingAsset,
            _outputToken,
            _maxIn,
            underlyingReceived,
            targetReceived
        );

        // Verify we got at least the required amount
        if (targetReceived < _amount) {
            revert InsufficientOutputAfterSwap(_amount, targetReceived);
        }

        // Handle any leftover tokens
        uint256 leftover = targetReceived - _amount;
        if (leftover > 0) {
            ERC20(_outputToken).safeTransfer(msg.sender, leftover);
        }

        // Return the amount of PT tokens used (assumed to be _maxIn for simplicity)
        // In practice, this could be calculated more precisely based on actual execution
        return _maxIn;
    }

    /**
     * @notice Executes a Pendle PT swap using SDK-generated transaction data
     * @param ptToken The PT token being swapped
     * @param ptAmount Amount of PT tokens to swap
     * @param target Target contract address from Pendle SDK
     * @param swapData Transaction data from Pendle SDK
     * @return actualUnderlyingOut Actual amount of underlying tokens received
     */
    function _executePendleSwap(
        address ptToken,
        uint256 ptAmount,
        address target,
        bytes memory swapData
    ) internal returns (uint256 actualUnderlyingOut) {
        // Call PendleSwapUtils library function directly
        // Note: Errors from the library will bubble up automatically
        return
            PendleSwapUtils.executePendleSwap(
                ptToken,
                ptAmount,
                target,
                swapData
            );
    }

    /**
     * @notice Executes an Odos swap using pre-calculated swap data
     * @param inputToken The input token address
     * @param outputToken The output token address
     * @param inputAmount The amount of input tokens to swap
     * @param minOutputAmount The minimum amount of output tokens required
     * @param target Target contract address from Odos API
     * @param swapData Transaction data from Odos API
     * @return actualOutputAmount Actual amount of output tokens received
     */
    function _executeOdosSwap(
        address inputToken,
        address outputToken,
        uint256 inputAmount,
        uint256 minOutputAmount,
        address target,
        bytes memory swapData
    ) internal returns (uint256 actualOutputAmount) {
        // Approve tokens to Odos router
        ERC20(inputToken).forceApprove(target, inputAmount);

        // Record output token balance before swap
        uint256 outputBalanceBefore = ERC20(outputToken).balanceOf(
            address(this)
        );

        // Execute Odos swap
        (bool success, bytes memory result) = target.call(swapData);
        if (!success) {
            if (result.length > 0) {
                assembly {
                    let resultLength := mload(result)
                    revert(add(32, result), resultLength)
                }
            }
            revert OdosSwapFailed("Odos swap execution failed");
        }

        // Calculate actual output received
        uint256 outputBalanceAfter = ERC20(outputToken).balanceOf(
            address(this)
        );
        actualOutputAmount = outputBalanceAfter - outputBalanceBefore;

        // Verify minimum output
        if (actualOutputAmount < minOutputAmount) {
            revert InsufficientOutputAfterSwap(
                minOutputAmount,
                actualOutputAmount
            );
        }

        return actualOutputAmount;
    }

    /**
     * @notice Helper function to check if a token is a PT token
     * @dev This can be used by external callers to determine if they should use this contract
     * @param token The token address to check
     * @return isPT True if the token appears to be a PT token
     */
    function isPTToken(address token) external view returns (bool isPT) {
        // Simple check - try to call SY() method which PT tokens should have
        try this.checkPTInterface(token) returns (bool result) {
            return result;
        } catch {
            return false;
        }
    }

    /**
     * @notice External function to check PT interface (called by isPTToken)
     * @param token The token address to check
     * @return True if token implements PT interface
     */
    function checkPTInterface(address token) external view returns (bool) {
        // Try to call SY() method - PT tokens should have this
        (bool success, bytes memory data) = token.staticcall(
            abi.encodeWithSignature("SY()")
        );

        // Check if call was successful and returned a valid address (not zero)
        if (!success || data.length != 32) {
            return false;
        }

        address syAddress = abi.decode(data, (address));
        return syAddress != address(0);
    }

    /**
     * @notice Get the contract version for identification
     * @return version The version string
     */
    function version() external pure returns (string memory) {
        return "FlashMintLiquidatorAaveBorrowRepayPTOdos-v1.0.0";
    }
}
