pragma solidity ^0.8.20;

import "../IOracleWrapper.sol";
import "./ILiquityV2OracleAggregatorV3Interface.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";

/**
 * @title BaseLiquityV2Wrapper
 * @dev Abstract contract that implements the IOracleWrapper interface for Liquity V2 oracles
 * Provides common functionality for all Liquity V2-compatible oracle wrappers
 */
abstract contract BaseLiquityV2Wrapper is IOracleWrapper, AccessControl {
    /* Core state */

    uint256 public constant LIQUITY_V2_BASE_CURRENCY_UNIT = 10 ** 18;
    uint8 public constant BASE_CURRENCY_DECIMALS = 18; // Decimals matching LIQUITY_V2_BASE_CURRENCY_UNIT
    uint256 public constant LIQUITY_V2_HEARTBEAT = 4 hours;
    address private immutable _baseCurrency;
    uint256 public immutable BASE_CURRENCY_UNIT;
    uint256 public heartbeatStaleTimeLimit = 30 minutes;

    /* Roles */

    bytes32 public constant ORACLE_MANAGER_ROLE =
        keccak256("ORACLE_MANAGER_ROLE");

    /* Errors */

    error PriceIsStale();
    error InvalidPrice();
    error FeedNotSet(address asset);
    error DecimalsMismatch(
        address feed,
        uint8 feedDecimals,
        uint8 expectedDecimals
    );

    /**
     * @dev Constructor that sets the base currency and base currency unit
     * @param baseCurrency The address of the base currency (zero address for USD)
     * @param _baseCurrencyUnit The decimal precision of the base currency (e.g., 1e8 for USD)
     */
    constructor(address baseCurrency, uint256 _baseCurrencyUnit) {
        _baseCurrency = baseCurrency;
        BASE_CURRENCY_UNIT = _baseCurrencyUnit;
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ORACLE_MANAGER_ROLE, msg.sender);
    }

    /**
     * @notice Returns the base currency address
     * @return Returns the base currency address
     */
    function BASE_CURRENCY() external view override returns (address) {
        return _baseCurrency;
    }

    /**
     * @notice Gets the price information for an asset
     * @param asset The address of the asset to get the price for
     * @return price The price of the asset in base currency units
     * @return isAlive Whether the price feed is considered active/valid
     */
    function getPriceInfo(
        address asset
    ) public view virtual override returns (uint256 price, bool isAlive);

    /**
     * @notice Gets the current price of an asset
     * @param asset The address of the asset to get the price for
     * @return The current price of the asset
     */
    function getAssetPrice(
        address asset
    ) external view virtual override returns (uint256) {
        (uint256 price, bool isAlive) = getPriceInfo(asset);
        if (!isAlive) {
            revert PriceIsStale();
        }
        return price;
    }

    /**
     * @dev Converts a price from Liquity V2 decimals to base currency decimals
     * @param price The price in Liquity V2 decimals
     * @return The price in base currency decimals
     */
    function _convertToBaseCurrencyUnit(
        uint256 price
    ) internal view returns (uint256) {
        return (price * BASE_CURRENCY_UNIT) / LIQUITY_V2_BASE_CURRENCY_UNIT;
    }

    /**
     * @notice Sets the heartbeat stale time limit
     * @param _newHeartbeatStaleTimeLimit The new heartbeat stale time limit
     */
    function setHeartbeatStaleTimeLimit(
        uint256 _newHeartbeatStaleTimeLimit
    ) external onlyRole(ORACLE_MANAGER_ROLE) {
        heartbeatStaleTimeLimit = _newHeartbeatStaleTimeLimit;
    }
}

